from app import dbclass File(db.Model):    __tablename__ = 'file'    id = db.Column(db.Integer, primary_key=True)    folder_id = db.Column(db.Integer, db.ForeignKey('folder.id'))    name = db.Column(db.String(128))    modified = db.Column(db.DateTime)    checksum = db.Column(db.String(32))class Folder(db.Model):    __tablename__ = 'folder'    id = db.Column(db.Integer, primary_key=True)    folder_id = db.Column(db.Integer, db.ForeignKey('folder.id'))    name = db.Column(db.String(128))    folder_contents = db.relationship('Folder', lazy=True, uselist=True)    file_contents = db.relationship('File', lazy=True, uselist=True)